  // 测试用例
  it(&#39;测试/getString.json请求&#39;, ( done ) =&gt; {
      request
        .get(&#39;/getString.json&#39;)
        .expect(200)
        .end(( err, res ) =&gt; {
            // 断言判断结果是否为object类型
            expect(res.body).to.be.an(&#39;object&#39;)
            expect(res.body.success).to.be.an(&#39;boolean&#39;)
            expect(res.body.data).to.be.an(&#39;string&#39;)
            done()
        })
  })
})

